    const [movieDetails , setMovieDetails] = useState({});

    useEffect(()=>{
        console.log('Params',id)
        getMovieDetails()
    },[])

    const getMovieDetails = ()=> {
        axios.get(`https://api.themoviedb.org/3/movie/${id}?language=en-US`,options)
        .then((res)=>{
            console.log(res.data)
            setMovieDetails(res.data)
        })
        .catch()
    }
    return (
