import React, {useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

import './MovieDetails.scss'; 

//const url ='https://api.themoviedb.org/3/movie/447365?language=en-US';
import './MovieDetails.scss';

const options = {
    method: 'GET',
    headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI4MTFiOGU2ZDBiMWZiMTk2NmFmZTgzYzYxMGE0NGEzMSIsInN1YiI6IjY0YWU1MjlhMWNmZTNhMDEzYjMzYWU1YiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.P1fl3lkiueoLuDc_GADzLaiTDqvPtDziC1X3oeDUTqE'
    }
};

const MovieDetails = () => {
    const { id } = useParams()
    const [movieDetails , setMovieDetails] = useState({});
    const [movieCredits, setMovieCredits] = useState([])

    useEffect(()=>{
        console.log('Params',id)
        getMovieDetails()
        getMovieCredits();
    },[])

    const getMovieDetails = ()=> {
        axios.get(`https://api.themoviedb.org/3/movie/${id}?language=en-US`,options)
        .then((res)=>{
            console.log(res.data)
            setMovieDetails(res.data)
        })
        .catch()
    }
    //https://api.themoviedb.org/3/movie/3683/credits?language=en-US
    const getMovieCredits = ()=>{
        axios.get(`https://api.themoviedb.org/3/movie/${id}/credits?language=en-US`,options)
        .then((res)=>{
            console.log(res.cast)
            setMovieCredits(res.cast)
        })
        .catch()
    }
    return (
        <div className='movie-details-main'>
            <div className='movie-poster'>
                <img src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}  alt="movie poster"/>
            </div>
            <div className='movie-content-main'>
                <h2>Details</h2>
                <div className='movie-content'>
                    <div className='movie-content-item'>
                        <h2>{movieDetails.title}</h2>
                        <h3>{movieDetails.tagline}</h3>
                        <p>{movieDetails.overview}</p>
                        <a href={movieDetails.homepage} target='_blank' rel="noreferrer">Learn More</a>
                    </div>
                    <div className='movie-content-item2'>
                        <p><strong>Budget: {movieDetails.budget}</strong></p>
                        <p><strong>Popularity: {movieDetails.popularity}</strong></p>
                        <p><strong>Release Date: {movieDetails.release_date}</strong></p>
                        <p><strong>Revenue: {movieDetails.revenue}</strong></p>
                    </div>
                    <div className='movie-content-item4'>
                        {
                            movieDetails.spoken_languages.map((lang)=>(
                                <p key={lang.english_name}>{lang.name}</p>
                            ))

                        }
                    </div>
                    <div className='movie-content-item3'>
                        {
                            movieDetails.production_companies.map((item)=>(
                                <div>
                                    <img src={`https://image.tmdb.org/t/p/w500${item.logo_path}`} alt="company logo" />
                                </div>
                            ))
                        }
                    </div>
                    <div>1</div>
                    <div>1</div>
                </div>
            </div>
        </div>
    )
}

export default MovieDetails;
