import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

import MovieCard from '../MovieCard/MovieCard';
import Pagination from '../../shared/Pagination/Pagination';

import './Movies.scss';


const options = {
    method: 'GET',
    headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI4MTFiOGU2ZDBiMWZiMTk2NmFmZTgzYzYxMGE0NGEzMSIsInN1YiI6IjY0YWU1MjlhMWNmZTNhMDEzYjMzYWU1YiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.P1fl3lkiueoLuDc_GADzLaiTDqvPtDziC1X3oeDUTqE'
    }
};

const Movies = () => {
    const [movies, setMovies] = useState([]);
    const {page} = useParams()
    useEffect(() => {
        getMovies()
    }, [])


    //discover, favorite, https://api.themoviedb.org/3/discover/movie?&adult=true&language=en-US&page=1  https://api.themoviedb.org/3/movie/popular?language=en-US&page=1
    const getMovies = () => {
        axios.get(`https://api.themoviedb.org/3/movie/popular?language=en-US&page=${page ? page : 1}`, options)
        .then(response => {
            setMovies(response.data.results)
            console.log(response)
        })
        .catch((err)=>  console.error(err.message));
    }


    return (
        <div className='movie-container'>
            {
                movies && movies.map((item) => (
                    <MovieCard data={item} key={item.id}/> 

                ))
            }
            <Pagination />
        </div>
    )
}

export default Movies;
