import React, { useEffect, useState } from 'react';
import './Movies.scss';
import axios from 'axios';

const options = {
    method: 'GET',
    headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI4MTFiOGU2ZDBiMWZiMTk2NmFmZTgzYzYxMGE0NGEzMSIsInN1YiI6IjY0YWU1MjlhMWNmZTNhMDEzYjMzYWU1YiIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.P1fl3lkiueoLuDc_GADzLaiTDqvPtDziC1X3oeDUTqE'
    }
};

const Movies = () => {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        getMovies()
    }, [])



    const getMovies = () => {
        axios.get('https://api.themoviedb.org/3/discover/movie?&language=en-US&page=1', options)
        .then(response => {
            setMovies(response.data.results)
            console.log(response)
        })
        .catch((err)=>  console.error(err.message));
    }





    return (
        <div>
            {
                movies && movies.map((item) => (
                    <div key={item.id}>
                        <img src={`https://www.themoviedb.org/p/t/w500 ${item.poster_path}`} alt={item.title}/>
                        <p>{item.title}</p> 
                    </div>

                ))
            }
        </div>
    )
}

export default Movies;
