const StarRating = ({ rating }) => {
    // Round the rating to the nearest 0.5
    const roundedRating = Math.round(rating * 2) / 2;

    return (
        <div className="star-rating">
            {[1, 2, 3, 4, 5].map((star) => (
                <span
                    key={star}
                    className={`star ${star <= roundedRating ? 'filled' : 'empty'}`}
                >{roundedRating}</span>
            ))}
        </div>
    );
};

export default StarRating;
